settings.css:
#Nidurteljari {
  position: absolute;
  font: 2em "Berthold Akzidenz Grotesk BE";
  top: 830px;
  left: 230px;
  width: 1450px;
  padding-top: 20px;
  border-top: 1px solid #095376;
  text-align: center;
  color: #095376;
  font-size: 60pt;
  line-height: 70pt;
  font-weight: bold;
}
#Nidurteljari-countdown-timer {
  font-size: 80pt;
}

#Nafn {
position: absolute;
bottom: 50px;
left: 0px;
width: 100%;
right: 0;
background: transparent;
color: black;
font-family: Raleway;
display: flex;
justify-content: center;
}

#Nafn .outer {
border: 4px solid white;
padding: 6px;
}

#Nafn .inside {
padding: 8px 24px;
background: white;
display: flex;
flex-direction: column;
}

#Nafn .inside h2 {
font-weight: bold;
font-size: 40px;
text-align: center;
text-transform: uppercase;
padding: 0;
margin: 0;
line-height: 120%;
}

#Nafn .inside h4 {
font-weight: normal;
font-size: 18px;
padding: 0;
margin: 0;
text-align: center;
line-height: 125%;
text-transform: uppercase;
}

#Minnisvers {
    position: absolute;
    font: 2em "Berthold Akzidenz Grotesk BE";
    top: 850px;
    right: 0px;
}
.bok-holder {
  background: rgba(0,68,105,0.6);
  line-height: 60px;
  font-size: 60px;
  color: white;
  padding: 10px 60px 0px 30px;
}
.vers-holder {
    background: rgba(33,29,29,0.6);
    font-size: 30px;
    line-height: 30px;
    color: white;
    padding: 10px 60px 0px 40px;
}

#Forn {
position: absolute;
top: 50px;
left: 95px;
border: 4px solid white;
padding: 6px;
background: transparent;
color: black;
font-family: Raleway;
}

#Forn .inside {
padding: 6px 24px;
background: white;
display: flex;
flex-direction: column;
}

#Forn .inside h2 {
font-weight: bold;
font-size: 24px;
text-align: center;
text-transform: uppercase;
padding: 0;
margin: 0;
}

#Forn .inside h4 {
font-weight: normal;
font-size: 18px;
padding: 0;
margin: 0;
text-align: center;
line-height: 125%;
}

.box-before {
  margin-bottom: 15px;
  width: 160px;
  height: 112px;
  margin-right: -37px;
  /* ---------- THEME COLOR ---------- */
  background-image: -webkit-radial-gradient(200px 50%, circle, rgba(0, 0, 0, 0) 0, rgba(0, 0, 0, 0) 75px, rgba(0,68,105,1) 77px);
  /* ---------- END THEME COLOR ---------- */
}
.box-after {
  /* ---------- THEME COLOR ---------- */
  background-image: -webkit-radial-gradient(-40px 50%, circle, rgba(0, 0, 0, 0) 0, rgba(0, 0, 0, 0) 75px, rgba(0,68,105,1) 77px);
  /* ---------- END THEME COLOR ---------- */
  margin-bottom: 15px;
  width: 100px;
  height: 112px;
  margin-left: -37px;
}

#Lög {
  position: absolute;
  color: white;
  font: 2em "Berthold Akzidenz Grotesk BE";
  top: 100px;
  left: 0px;
}

#Lög > div { display: inline-block; }

.box-logo {
  width: 141px;
  height: 141px;
  background: url('uploads/logo.png') 0px 0px no-repeat;
  background-size: contain;
}

.box-name {
  margin-bottom: 15px;
  height: 112px;
  position: relative;
  margin-left: -6px;
  width: 700px;
}
.holder { position: absolute; }
.name-holder {
  position: relative;
  background: linear-gradient(to right, rgba(207,209,210,0.6) 0%,rgba(235,235,237,0.6) 50%,rgba(183,185,185,0.6) 100%);
  line-height: 102px;
  font-size: 60px;
  color: rgba(0,68,105,1);
  padding: 10px 30px 0px 20px;
}
.name-triangle {
    position: absolute;
    right: -112px;
    border-width: 0 112px 112px 0px;
    top: 0;
    border-style: solid;
    border-color: transparent transparent rgba(183,185,185,0.6) transparent;
}

